Select * from (SELECT occurrence_time as timestamp,monitor_value,reasons FROM t_performance_work_rt as test 
WHERE cellid = #{cellname} and monitor_content='新PRB利用率(4次连续)'

	 AND test.occurrence_time >=#{starttime} and test.occurrence_time <=#{endtime}


	AND TO_DAYS(NOW())-TO_DAYS(test.occurrence_time) <=76


	AND TO_DAYS(NOW())-TO_DAYS(test.occurrence_time) <=80


	AND TO_DAYS(NOW())-TO_DAYS(test.occurrence_time) =74


	AND TO_DAYS(NOW())-TO_DAYS(test.occurrence_time) =75

	) as counts order by timestamp;

