mapper.select:
select
occurrence_time,cellid,boutique_level,limit_times,belong_area,reasons,
monitor_content,monitor_value,alerm_level,complete_time
from
t_performance_work


		occurrence_time like #{occurrence_time}


		and cellid like #{cellid}


		and belong_area like #{belong_area}


		and monitor_content like #{monitor_content}


limit 1000
