mapper.select:

select
e_utrancell,network_name,used_band,cover_scene,station_name,group_type
from
table_cell_base_station, t_groupBaseInfo where
table_cell_base_station.network_name= t_groupBaseInfo.cell_code 

    and table_cell_base_station.e_utrancell like #{e_utrancell}


    and table_cell_base_station.cover_scene like #{cover_scene}


    and t_groupBaseInfo.group_type like #{group_type}

limit 100
